import { useState } from 'react'
import { User } from '@/types'
import { cn } from '@/lib/utils'

interface UserCardProps {
  user: User
  onFollow?: (userId: string) => void
  onUnfollow?: (userId: string) => void
  isFollowing?: boolean
  className?: string
}

export function UserCard({ user, onFollow, onUnfollow, isFollowing = false, className }: UserCardProps) {
  const [localFollowing, setLocalFollowing] = useState(isFollowing)

  const handleFollowToggle = () => {
    if (localFollowing) {
      onUnfollow?.(user.id)
    } else {
      onFollow?.(user.id)
    }
    setLocalFollowing(!localFollowing)
  }

  return (
    <article
      className={cn(
        'bg-white dark:bg-gray-800 rounded-lg shadow-md p-4 flex items-center gap-4',
        'hover:shadow-lg transition-shadow duration-200',
        className
      )}
    >
      <img
        src={user.avatarUrl}
        alt={`Avatar for ${user.name}`}
        className="w-16 h-16 rounded-full object-cover"
        onError={(e) => {
          e.currentTarget.src = '/default-avatar.png'
        }}
      />
      <div className="flex-1">
        <h3 className="font-semibold text-gray-900 dark:text-white">{user.name}</h3>
        <p className="text-sm text-gray-600 dark:text-gray-400">{user.email}</p>
      </div>
      <button
        onClick={handleFollowToggle}
        className={cn(
          'px-4 py-2 rounded-md font-medium transition-colors',
          localFollowing
            ? 'bg-gray-200 hover:bg-gray-300 text-gray-800'
            : 'bg-blue-600 hover:bg-blue-700 text-white'
        )}
        aria-label={localFollowing ? `Unfollow ${user.name}` : `Follow ${user.name}`}
      >
        {localFollowing ? 'Following' : 'Follow'}
      </button>
    </article>
  )
}

import { render, screen, fireEvent } from '@testing-library/react'
import { describe, it, expect, vi } from 'vitest'
import { UserCard } from './UserCard'
import { User } from '@/types'

describe('UserCard', () => {
  const mockUser: User = {
    id: '1',
    name: 'John Doe',
    email: 'john@example.com',
    avatarUrl: 'https://example.com/avatar.jpg'
  }

  it('renders user information', () => {
    render(<UserCard user={mockUser} />)

    expect(screen.getByText('John Doe')).toBeInTheDocument()
    expect(screen.getByText('john@example.com')).toBeInTheDocument()
    expect(screen.getByAltText('Avatar for John Doe')).toBeInTheDocument()
  })

  it('calls onFollow when Follow button is clicked', () => {
    const onFollow = vi.fn()
    render(<UserCard user={mockUser} onFollow={onFollow} />)

    const followButton = screen.getByRole('button', { name: /follow john doe/i })
    fireEvent.click(followButton)

    expect(onFollow).toHaveBeenCalledWith('1')
  })

  it('displays Following state when isFollowing is true', () => {
    render(<UserCard user={mockUser} isFollowing={true} />)

    expect(screen.getByRole('button', { name: /following/i })).toBeInTheDocument()
  })
})

