import { renderHook, waitFor } from '@testing-library/react'
import { describe, it, expect, vi } from 'vitest'
import { useDebounce } from './useDebounce'

describe('useDebounce', () => {
  it('returns initial value immediately', () => {
    const { result } = renderHook(() => useDebounce('initial', 500))
    expect(result.current).toBe('initial')
  })

  it('debounces value updates', async () => {
    const { result, rerender } = renderHook(
      ({ value }) => useDebounce(value, 500),
      { initialProps: { value: 'initial' } }
    )

    // Rapid updates
    rerender({ value: 'update 1' })
    rerender({ value: 'update 2' })
    rerender({ value: 'final' })

    // Should still be initial before delay
    expect(result.current).toBe('initial')

    // Should update after delay
    await waitFor(
      () => expect(result.current).toBe('final'),
      { timeout: 600 }
    )
  })

  it('works with different types', () => {
    const { result: numResult } = renderHook(() => useDebounce(42, 100))
    expect(numResult.current).toBe(42)

    const { result: objResult } = renderHook(() => 
      useDebounce({ key: 'value' }, 100)
    )
    expect(objResult.current).toEqual({ key: 'value' })
  })
})
